sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {TransformApiHelpers} params.helpers
 */
function evaluateProperty({api,helpers}) {
    let contactField = api.data.record.form.fields.contact;
    let consumerField = api.data.record.form.fields.consumer;
    //if no contact field on form, or they can't read the field, don't show the card
    if (!contactField || !contactField.dictionary.canRead || api.data.record.isNew) {return true;} else if (!contactField.referenceSysId && !consumerField.referenceSysId) {return false;} else if (!contactField.referenceSysId) {return true;} return false;}
