sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
async function handler({api, event, helpers, imports}) {
    const actionItems = event?.payload?.data?.GlideViewQuery_Query?.uiView?.glideLayoutItem?._query?.GlideUxActionLayout_Query?.getActionLayout?.actionLayoutItems;
    const length = actionItems.length;
    let shnActionItem = null;
    for(let i = 0;i < length;i++){
        const actionItem = actionItems[i];
        if(actionItem.name && actionItem.name.toLowerCase() === "special handling notes"){
            shnActionItem = actionItem;
            break;
        }
    }
    api.setState('shnActionSysId', shnActionItem?.sysId);
}
