sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
async function handler({api, event, helpers, imports}) {
    if(api.data.get_special_handling_notes_1 && api.data.get_special_handling_notes_1.output){
        api.setState('Totalmsg', api.data.get_special_handling_notes_1.output.data.GlideSHN_Query.totalMsg);
    } 
    if(api.data.get_special_handling_notes_1.output.data.GlideSHN_Query.totalMsg == 0 && api.state.showSHNAlert){
        api.emit('NOW_UXF_PAGE#ADD_NOTIFICATIONS', {
                            items: [{
                                id: 'no_shn',
                                status: 'info',
                                icon: 'circle-exclamation-outline',
                                content: await helpers.translate('No special handling notes available'),
                                action: {
                                    type: 'dismiss'
                                }
                            }]
                        });
    }
}
